 echo "Changing Mac Address..."; $NewMacAddress = (0..5 | ForEach-Object { '{0:x}{1:x}' -f (Get-Random -Minimum 0 -Maximum 15),(Get-Random -Minimum 0 -Maximum 15)})  -join ':';$applymac = $NewMacAddress.Replace(($NewMacAddress.Substring(0,2)),'02').ToUpper().ToString()  ;$adapter= Get-NetAdapter -Name "*" -Physical | Where Status -eq up | Format-List -Property "Name" | Out-String; Set-NetAdapter -Name $adapter.replace("Name : ","").replace("`n","").replace("`r","") -MacAddress $applymac -Confirm:$false;echo "Wating 10 Seconds Until Everything Applies";sleep 10; echo "Done";echo "New MacAddress : $applymac";ping 8.8.8.8
